mapper.select:
select * from ( select A.board_seq ,nvl(B.username, 'noname') username ,A.userid ,A.title ,A.contents --내용 ,nvl(A.hit , 0) hit --조회수 ,A.group_id ,A.depth ,A.g_level ,to_char(A.regdate, 'yyyy-mm-dd') regdate ,row_number() over (order by A.group_id desc, A.g_level asc) as num ,ceil(row_number () over (order by A.group_id desc, A.g_level asc)/10-1) as pg from tl_board A left outer join tl_member B on A.userid=B.userid where delyn='N'
and (title like '%'||#{key}||'%' or contents like '%'||#{key}||'%')
and title like '%'||#{key}||'%'
and contents like '%'||#{key}||'%'
)where pg=#{pg}

mapper.insert:
select nvl(max(board_seq),0)+1 as board_seq from tl_board
insert into tl_board( board_seq ,title ,contents ,userid ,hit ,group_id ,depth ,g_level ,regdate ,delyn ,ip_addr
,
${item}
)values( #{board_seq} ,#{title} ,#{contents} ,#{userid} ,0 ,#{board_seq} ,0 ,1 ,sysdate ,'N' ,'192.168.0.1' --수정 해야함.
,
'${item}'
)

mapper.update:
 
	update tl_board set
	   g_level=g_level+1
	where group_id=#{group_id}
	and g_level>#{g_level}

update 	tl_board
	set title = #{title}
	,contents = #{contents}
	,filename1 = #{filename1}
	,filename2 = #{filename2}
	,filename3 = #{filename3}
where board_seq=#{board_seq}

update	tl_board
set delyn='Y'
where board_seq=#{board_seq}

mapper.select:
select
	userid from tl_board
	where board_seq=#{seq}
