mapper.select:
select count(*) 
from tl_member
where email = #{email}

mapper.insert:
 	
		select nvl(max(member_seq), 0)+1 as member_seq
		from tl_member
	
 
 	insert into tl_member(
		member_seq
		,userid
		,email
		,password
		,username
		,base_address
		,detail_address
		,mobile
		,zipcode
		,regdate
		,member_auth
		,profile_image
 	)values(
		#{member_seq}
		,#{userid}
		,#{email}
		,#{password}
		,#{username}
		,#{base_address}
		,#{detail_address}
		,#{mobile}
		,#{zipcode}
		,#{regdate}
		,#{member_auth}
		,#{profile_image}
 	)
